(function(){tinymce.PluginManager.requireLangPack('jfilebrowser');tinymce.create('tinymce.plugins.jfilebrowser',{init:function(ed,url){ed.addCommand('mcejfilebrowser',function(){ed.windowManager.open({file:url+'/filebrowser.php',width:680+parseInt(ed.getLang('jfilebrowser.delta_width',0)),height:440+parseInt(ed.getLang('jfilebrowser.delta_height',0)),inline:1},{plugin_url:url,some_custom_arg:'custom arg'})});ed.addButton('jfilebrowser',{title:'jfilebrowser',cmd:'mcejfilebrowser',image:url+'/img/jfilebrowser.png'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('jfilebrowser',n.nodeName=='IMG')})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'jfilebrowser plugin',author:'Some author',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/jfilebrowser',version:"1.0"}}});tinymce.PluginManager.add('jfilebrowser',tinymce.plugins.jfilebrowser)})();